import React, { useEffect, useState } from 'react';
import '../styles/parallax.css';

const HeroSection = ({ scrollToSection }) => {
    const [scrollOffset, setScrollOffset] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
            setScrollOffset(window.scrollY);
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <section
            id="hero"
            className="h-screen relative flex justify-end overflow-hidden bg-cover bg-no-repeat bg"
        >
            <div className="absolute inset-0 bg-black opacity-50 z-0"></div>
            <div className="absolute inset-y-0 left-20 flex flex-col items-start pl-4 z-10 w-1/2">
                <div
                    className={`text-right mb-4 ${
                        scrollOffset >= 100 ? 'animate-slideLeftH1' : ''
                    }`}
                >
                    <h1 className="text-4xl font-bold text-white mb-2">Julius Wong</h1>
                </div>
                <div
                    className={`text-right mb-4 ${
                        scrollOffset >= 200 ? 'animate-slideLeftH2' : ''
                    }`}
                >
                    <h2 className='text-blue-500'>Design and Full-Stack Dev</h2>
                </div>
                <div
                    className={`text-right ${
                        scrollOffset >= 300 ? 'animate-slideLeftButton' : ''
                    }`}
                >
                    <button
                        onClick={() => scrollToSection('about')}
                        className="bg-white text-blue-500 px-4 py-2 rounded-full mt-2"
                    >
                        About Me
                    </button>
                </div>
            </div>
        </section>
    );
};

export default HeroSection;
